            window.addEventListener('resize', function(){ initJS(); });
            window.addEventListener('click', function(){ initJS(); });
            window.addEventListener('mouseover', function(){ initJS(); });
            window.addEventListener('touchstart', function(){ initJS(); }, false);
            document.addEventListener('hyperlocal:load', function(){ initJS(); }, false);

            function initJS()
            {
                if( initedJS ) return false;

                initedJS = true;

                loadJS('https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.min.js', 0, 1);
                loadJS('https://cdnjs.cloudflare.com/ajax/libs/alpinejs/2.8.0/alpine-ie11.min.js', 0, 1);
                loadJS('https://cdnjs.cloudflare.com/ajax/libs/lazysizes/5.3.2/lazysizes.min.js', 1, 0);
